import React, { useState } from "react";
import axios from "axios";

const SignUpContent = ({setShowSignUp, setShowLogIn}) => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [username, setUsername] = useState("");

  const handleEmailChange = (e) => {
    setEmail(e.target.value);
  };

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
  };

  const handleUsernameChange = (e) => {
    setUsername(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Trim the values before submitting the form
    const trimmedEmail = email.trim();
    const trimmedUsername = username.trim();

    try {
      const response = await axios.post('http://localhost:8000/register', {
        email: trimmedEmail,
        password,
        username: trimmedUsername
      });

      // If the request is successful, you can handle the response
      console.log("Registration successful:", response.data);
      setShowLogIn(true);
      setShowSignUp(false);

    } catch (error) {
      // If there's an error, handle it (e.g., display an error message)
      console.error("Error:", error.response.data);
    }
  };
  return (
    <div>
      <h2>Sign Up</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Username (first and last name):</label>
          <input type="username" value={username} onChange={handleUsernameChange} />
        </div>
        <div>
          <label>Email:</label>
          <input type="email" value={email} onChange={handleEmailChange} />
        </div>
        <div>
          <label>Password:</label>
          <input type="password" value={password} onChange={handlePasswordChange} />
        </div>
        <button type="submit">Register As A New User</button>
      </form>
    </div>
  );
};

export default SignUpContent;
