import React, { useState } from "react";
import axios from "axios";

const LogInContent = ({ onLogin, setHomePageVisible, setShowLogIn }) => {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [error, setError] = useState("");

    const handleEmailChange = (e) => {
        setEmail(e.target.value);
    };

    const handlePasswordChange = (e) => {
        setPassword(e.target.value);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post("http://localhost:8000/login", {
                email,
                password,
            });

            useEffect(() => {
                axios.get('http://localhost:8000/')
                  .then(res => {
                    setUsername(res.data.user.email)
                    setAuthenticated(true)
                    setHomePageVisible(true)
                    setWelcomePage(false)
                  })
                  .catch(error => {
                    console.error('Error fetching data:', error);
                  });
              }, []);

        } catch (error) {
            if (error.response.status === 453) {
                setError("Error: Email not found.");
            } else if (error.response.status === 454) {
                setError("Error: Wrong password.");
            }
        }
    };

    return (
        <div style={{ textAlign: 'center' }}>
            <h2>Login</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Email:</label>
                    <input
                        type="email"
                        value={email}
                        onChange={handleEmailChange}
                        required
                    />
                </div>
                <br />
                <div>
                    <label>Password:</label>
                    <input
                        type="password"
                        value={password}
                        onChange={handlePasswordChange}
                        required
                    />
                </div>
                <button type="submit">Login</button>
                {error && <p style={{ color: 'red' }}>{error}</p>}
            </form>
        </div>
    );
};

export default LogInContent
