import React from 'react';
import SearchBar from './searchbar.js';
import ProfileRightSideBox from './profilersb.js';
import AdminProfileRightSideBox from './adminprofilersb.js';
import axios from 'axios';
import { useState, useEffect } from "react";


const ProfilePageContent = ({ users, reputation, usernameDisplay, username, setProfilePage, filteredTag, setWelcomePage, setFilteredTag, setAskQuestionPageVisible, setTagPageVisible, setAnswerPageVisible, setHomePageVisible, questions, setQuestions, answers, tags, searchOn, setSearch, searchString, setSearchString, setCurrentQuestionClicked }) => {
  const [createdby, setCreatedBy] = useState(new Date());
  const [accountType, setAccountType] = useState("");
  const [adminPage, showAdminPage] = useStateT

  useEffect(() => {
    axios.get('http://localhost:8000/createdBy', { params: { email: username } })
      .then(res => {
        const date = new Date(res.data.timestampOfCreation);
        setCreatedBy(date);
      }).catch(error => {
        console.error('Error fetching createdBy:', error);
      });

    axios.get('http://localhost:8000/accountType', { params: { email: username } })
      .then(res => {
        setAccountType(res.data.accountType)
      }).catch(error => {
        console.error('Error fetching data:', error);
      });
  }, [username]);

  const handleQuestionsClick = () => {
    setHomePageVisible(true);
    setAnswerPageVisible(false);
    setAskQuestionPageVisible(false);
    setTagPageVisible(false);
    setSearch(false);
    setFilteredTag([]);
    setProfilePage(false);
  };

  const handleTagsClick = () => {
    setTagPageVisible(true);
    setHomePageVisible(false);
    setAnswerPageVisible(false);
    setAskQuestionPageVisible(false);
    setSearch(false);
    setFilteredTag([]);
    setProfilePage(false);
  };

  const toggleSearch = (searchString) => {
    setFilteredTag([]);
    setSearchString(searchString);
    setSearch(true);
  };

  const toggleSignOut = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:8000/logout')
    } catch (error) {
      alert("Sign Out Unsuccessful")
      return;
    }
    setHomePageVisible(false);
    setWelcomePage(true);
  };

  const handleProfilePage = () => {
    setHomePageVisible(false);
    setProfilePage(true);
  };

  return (
    <div className="grid-container">
      <div className="header-stack">
        <h1 style={{ width: '50%', fontSize: '50px', textAlign: 'center' }}> FakeStackOverflow </h1>
        <h4 style={{ marginTop: '65px', marginRight: '20px' }}>Hello, {usernameDisplay}</h4>
        {username !== "Guest" ? (
          <>
            <button className="hyperlinkButton" onClick={handleProfilePage} style={{ marginRight: "6px" }}>Profile</button>
            <button className="hyperlinkButton" onClick={toggleSignOut} style={{ width: '80px', marginRight: '100px' }}>Sign Out</button>
          </>
        ) : (
          <button className="hyperlinkButton" onClick={toggleSignOut} style={{ width: '100px', marginRight: '100px' }}>Sign in/Sign up</button>
        )}
        <SearchBar onSearch={toggleSearch} />
      </div>
      <div className="left-side-bar">
        <button className={"hyperlinkButton"} onClick={handleQuestionsClick}>Questions</button>
        <br />
        <button className={"hyperlinkButton"} onClick={handleTagsClick}>Tags</button>
      </div>
      {accountType === "user" ? (
        <>
          <div className="right-side-box">
            <ProfileRightSideBox
              createdby={createdby}
              reputation={reputation}
              accountType={accountType}
            />
          </div>
        </>
      ) : (
        <div className="right-side-box">
          <Dateandnamemember createdby={createdby} />
          <p>Reputation: {reputation}</p>
          <p>Questions asked: </p>
          {users.map(user => (
            <p key={user.username}>
              <a href={`/profile/${user.username}`} key={user.username}>{user.username}</a>
            </p>
          ))}
        </div>
      )}
    </div>
  );
};

const Dateandnamemember = ({ createdby }) => {
  var date = createdby
  var currentDate = new Date();

  if (((currentDate - date) / (1000 * 60 * 60)) < 24) {
    var timeDiffSec = (currentDate - date) / 1000;
    var timeRn = 0;
    if (timeDiffSec < 60) {
      timeRn = Math.floor(timeDiffSec);
      return <p>Been a member for {timeRn} seconds</p>;
    } else if (timeDiffSec < 3600) {
      timeRn = Math.floor(timeDiffSec / 60);
      return <p>Been a member for  {timeRn} minutes</p>;
    } else {
      timeRn = Math.floor(timeDiffSec / 3600);
      return <p>Been a member for  {timeRn} hours</p>;
    }
  } else {
    var yearsDiff = currentDate.getFullYear() - date.getFullYear();
    if (yearsDiff < 1) {
      let timeDiffDays = Math.floor((currentDate - date) / (1000 * 60 * 60 * 24));
      return <p>Been a member for {timeDiffDays} days</p>;
    } else {
      var yearsDaysDiff = currentDate - new Date(date.getFullYear() + yearsDiff, date.getMonth(), date.getDate());
      let timeDiffDays = Math.floor(yearsDaysDiff / (1000 * 60 * 60 * 24));
      return <p>Been a member for {yearsDiff} years and {timeDiffDays} days</p>;
    }
  }
}

export default ProfilePageContent;
