import React, { useEffect } from "react";

const ContinueAsGuestContent = ({ setShowContinueAsGuest, setHomePageVisible }) => {
    // Use useEffect to execute the logic when the component mounts
    useEffect(() => {
        // Update state to hide ContinueAsGuestContent and show HomePageContent
        setShowContinueAsGuest(false);
        setHomePageVisible(true);
    }, [setShowContinueAsGuest, setHomePageVisible]); // Make sure to include dependencies in the dependency array if needed

    // Since this component doesn't render anything, you can return null
    return null;
};

export default ContinueAsGuestContent;
