import React, { useState } from "react";
import CreateNewUserContent from "./createnewuser";
import ContinueAsGuestContent from "./continueasguest";
import LogInContent from "./login";

// import bcrypt from 'bcrypt'

// console.time('hash')
// const password = 'Password1'
// const hash = await bcrypt.hash(password,10)
// console.log({
//     password,
//     hash
// })


const SignInContent = () => {
  const [currentPage, setCurrentPage] = useState(null);
  const [showCreateNewUser, setShowCreateNewUser] = useState(false);
  const [showContinueAsGuest, setShowContinueAsGuest] = useState(false);
  const [showLogIn, setShowLogIn] = useState(false);
  const [mainPageHidden, setMainPageHidden] = useState(false);

  const hideMainPage = () => {
    setMainPageHidden(true);
  };

  return (
    <div>
      {mainPageHidden ? null : (
        <div>
          <h1 style={{ textAlign: "center" }}>Welcome to FakeStackOverflow</h1>
          <p>Welcome to the new page!</p>

          <div style={{ display: "flex", justifyContent: "center" }}>
            <button onClick={() => { setShowCreateNewUser(true); hideMainPage(); }}>Create New User</button>
            <button onClick={() => { setShowContinueAsGuest(true); hideMainPage(); }}>Continue as Guest</button>
            <button onClick={() => { setShowLogIn(true); hideMainPage(); }}>Log In</button>
          </div>
        </div>
      )}

      {showCreateNewUser && <CreateNewUserContent />}
      {showContinueAsGuest && <ContinueAsGuestContent />}
      {showLogIn && <LogInContent />}
    </div>
  );
};

export default SignInContent;
