import React, { useState } from "react";
import LogInContent from "./login";
import ContinueAsGuestContent from "./continueasguest";
import SignUpContent from "./signup";

const SignInContent = ({setHomePageVisible}) => {
  const [currentPage, setCurrentPage] = useState(null);
  const [showLogIn, setShowLogIn] = useState(false);
  const [showContinueAsGuest, setShowContinueAsGuest] = useState(false);
  const [showSignUp, setShowSignUp] = useState(false);
  const [mainPageHidden, setMainPageHidden] = useState(false);
  const hideMainPage = () => {
    setMainPageHidden(true);
  };

  const handleSignUp = (credentials) => {
    // Implement your sign-up logic here
    console.log("Signing up with credentials:", credentials);
    // You can send a request to your server to create a new user
  };

  return (
    <div>
      {mainPageHidden ? null : (
        <div>
          <h1 style={{ textAlign: "center" }}>Welcome to FakeStackOverflow</h1>
          <p>Welcome to the new page!</p>

          <div style={{ display: "flex", justifyContent: "center" }}>
            <button onClick={() => { setShowLogIn(true); hideMainPage(); }}>Log In</button>
            <button onClick={() => { setShowContinueAsGuest(true); hideMainPage(); }}>Continue as Guest</button>
            <button onClick={() => { setShowSignUp(true); hideMainPage(); }}>Sign Up</button>
          </div>
        </div>
      )}

      {showLogIn && <LogInContent setHomePageVisible={setHomePageVisible} 
      setShowLogIn = {setShowLogIn}/>}

      {showContinueAsGuest && <ContinueAsGuestContent setHomePageVisible={setHomePageVisible} 
      setShowContinueAsGuest = {setShowContinueAsGuest}/>}

      {showSignUp && <SignUpContent setShowSignUp = {setShowSignUp}
      setShowLogIn = {setShowLogIn}
      />}
    </div>
  );
};

export default SignInContent;
